function udg_check_game_end_condition()
	rts_update_living_and_dead_counts()
	if gNumZombies==0 then
		return ig3d_GetLevelName__s(),"\nYour legion of undead have been destroyed!!\n\nPress space bar to restart scenario"
	end
	if gNumHumans==0 then
			return "Data/Levels/UDG/UDG_LivingInnHard","\nThe Undead Win Again!\n\nCongratulations!\n\nBut whats this?\nLooks like the living want a rematch\nand they're going to play rough.\n\nPress space bar to show them what you're made of."
	end
	return nil,""	
end
