function udg_check_game_end_condition()
	rts_update_living_and_dead_counts()
	if gNumZombies==0 then
		return ig3d_GetLevelName__s(),"\nYour legion of undead have been decimated!!\n\nPress space bar to restart scenario"
	end
	if gNumHumans==0 then
			return "Data/Levels/UDG/UDG_SnipersAlleyTwoHard","\nThis building has been ravaged.\n\nPress space bar to continue."
	end
	return nil,""	
end
