function udg_check_game_end_condition()
	rts_update_living_and_dead_counts()
	if gNumZombies==0 then
		return "Data/Levels/UDG/UDG_MainMenu","\nYour legion of undead have been destroyed!!\n\n Don't be discouraged\nYou can train in this room to become a true leader of undead armies.\n\nPress space bar to return to Main Menu."
	end
	if gNumHumans==0 then
			return "Data/Levels/UDG/UDG_MainMenu","\nThe Dead Win!!\nYou've got zombie skills!\n\nPress space bar to continue."
	end
	return nil,""	
end
