	gObjects[i].cObj=make(ig3d_object, "AquaChar/chr"..tostring(i-1)..".wtf")
	setObjectInfo(gObjects[i].cObj, IG3D_NAME, "chr"..tostring(i-1))
	setObjectInfo(gObjects[i].cObj, IG3D_POSITION, (c*8)+1,0,r*8)
	setObjectInfo(gObjects[i].cObj, IG3D_ROTATION, 0,0,0)
	setObjectInfo(gObjects[i].cObj, IG3D_SIZE, 100,100,100)
	setObjectInfo(gObjects[i].cObj, IG3D_COLL, true)
	setObjectInfo(gObjects[i].cObj, IG3D_HUD, false)
	c=c+1
	if c==18 then
	c=1
	r=r+1
	end
	end
end

