function spin_all(x,y,z)
for i = 1,#gObjects,1 do
	local xa,ya,za=getObjectInfo(gObjects[i].cObj, IG3D_ROTATION)
	if x ~= 0 then xa=xa+x end
	if y ~= 0 then ya=ya+y end
	if z ~= 0 then za=za+z end
	
	setObjectInfo(gObjects[i].cObj, IG3D_ROTATION, xa,ya,za)
	end
end

function move_all(w,h,d)
for i = 1,#gObjects,1 do
	local x,y,z=getObjectInfo(gObjects[i].cObj, IG3D_POSITION)
	if w ~= 0 then x=x+w end
	if h ~= 0 then y=y+h end
	if d ~= 0 then z=z+d end
	
	if x > 151 then
	x=x-136
	z=z+8
	--Aqua_color_cycle(i)
	end
	if z > 120 then
	x=8
	z=z-128
	--Aqua_color_cycle(i)
	end
	
	setObjectInfo(gObjects[i].cObj, IG3D_POSITION, x,y,z)
	end
end






